style:
.genmedai-shortcuts-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 20px;
  margin-bottom: 20px;
}
.genmedai-card {
  text-decoration: none !important;
  background: #ffffff;
  border: 1px solid #ebf1f5;
  border-radius: 12px;
  padding: 18px;
  display: flex;
  align-items: center;
  transition: all 0.2s ease;
  cursor: pointer;
}
.genmedai-card:hover {
  border-color: #dce4ea;
  box-shadow: 0 5px 15px rgba(0,0,0,0.04);
  transform: translateY(-2px);
}
.genmedai-icon-box {
  width: 48px;
  height: 48px;
  border-radius: 10px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 16px;
  font-size: 20px;
  flex-shrink: 0;
}
.genmedai-text-box {
  display: flex;
  flex-direction: column;
}
.genmedai-title {
  color: #1f272e;
  font-size: 15px;
  font-weight: 600;
  margin-bottom: 2px;
  line-height: 1.2;
}
.genmedai-subtitle {
  color: #6c767e;
  font-size: 13px;
  font-weight: 400;
}
.genmedai-arrow {
  margin-left: auto;
  color: #aeb9c2;
  font-size: 18px;
  transition: color 0.2s;
}
.genmedai-card:hover .genmedai-arrow {
  color: #6c767e;
}

/* Blue Theme - Medicine */
.theme-blue .genmedai-icon-box {
  background: #eef4ff;
  color: #2b7afb;
}

/* Green Theme - Email */
.theme-green .genmedai-icon-box {
  background: #ebfaeb;
  color: #28a745;
}

/* Orange Theme - Contact */
.theme-orange .genmedai-icon-box {
  background: #fff8eb;
  color: #fd7e14;
}
